import React from 'react';
import TodoList from './TodoList';
const Form = (props) => { // function component
    const InputChangeHandler = (event) => {
        console.log(event.target.value);
        props.setInputValue(event.target.value);
    }
    const onSubmitClick = (event) =>{
        props.setTodos = [...props.set]
    }
    return (
        <div className="container mt-4">
            <h2>ADD LISTS</h2>
            <div className="list-section d-flex mt-4">
                <input type="text" onChange={InputChangeHandler} className="form-control w-25" />
                <button type="submit" onClicl={onSubmitClick} className="btn btn-primary ml-3 mb-2"> Submit </button>
            </div>
            <ul className="list-group">
                <TodoList />
            </ul>
        </div>
    )
}

export default Form;
