import React from 'react';

const Form = (props) => { // function component
    const InputChangeHandler = (event) => {
        console.log(event.target.value);
        props.setInputValue()
    }
    return (
        <div className="container mt-4">
            <h2>ADD LISTS</h2>
            <div className="list-section d-flex mt-4">
                <input type="text" onChange={InputChangeHandler} className="form-control w-25" />
                <button type="submit" className="btn btn-primary ml-3 mb-2"> Submit </button>
            </div>
            <ul className="list-group">
            </ul>
        </div>
    )
}

export default Form;
